						function login() {

						/* cookie getter/setter */
						var allCookies = {
						getItem: function (sKey) {
						if (!sKey || !this.hasItem(sKey)) { return null; }
						return unescape(document.cookie.replace(new RegExp("(?:^|.*;\\s*)" +
						escape(sKey).replace(/[\-\.\+\*]/g, "\\$&") +
						"\\s*\\=\\s*((?:[^;](?!;))*[^;]?).*"), "$1"));
						},
						setItem: function (sKey, sValue, vEnd, sPath, sDomain, bSecure) {
						if (!sKey ||
						/^(?:expires|max\-age|path|domain|secure)$/i.test(sKey)) { return;
						}
						var sExpires = "";
						if (vEnd) {
						switch (vEnd.constructor) {
						case Number:
						sExpires = vEnd === Infinity ? "; expires=Tue, 19 Jan 2038 03:14:07 GMT" :
						"; max-age=" + vEnd;
						break;
						case String:
						sExpires = "; expires=" + vEnd;
						break;
						case Date:
						sExpires = "; expires=" + vEnd.toGMTString();
						break;
						}
						}
						document.cookie = escape(sKey) + "=" + escape(sValue) + sExpires + (sDomain ? ";
						domain=" + sDomain : "") + (sPath ? "; path=" + sPath : "") +
						(bSecure ? "; secure" : "");
						},
						removeItem: function (sKey, sPath) {
						if (!sKey || !this.hasItem(sKey)) { return; }
						document.cookie = escape(sKey) + "=; expires=Thu, 01 Jan 1970 00:00:00 GMT" +
						(sPath ? "; path=" + sPath : "");
						},
						hasItem: function (sKey) {
						return (new RegExp("(?:^|;\\s*)" + escape(sKey).replace(/[\-\.\+\*]/g,
						"\\$&") + "\\s*\\=")).test(document.cookie);
						},
						keys: /* optional method: you can safely remove it! */ function () {
						var aKeys =
						document.cookie.replace(/((?:^|\s*;)[^\=]+)(?=;|$)|^\s*|\s*(?:\=[^;]*)?(?:\1|$)/g,
						"").split(/\s*(?:\=[^;]*)?;\s*/);
						for (var nIdx = 0; nIdx < aKeys.length; nIdx++) { aKeys[nIdx] =
						unescape(aKeys[nIdx]); }
						return aKeys;
						}
						};

						// converts cookie into js object
						function parseCookie(cookie) {

						cookie = cookie.split("&");
						var obj = {};

						for(var i = 0; i < cookie.length; i++) {

						var c = cookie[i].split("=");

						c[0] = c[0].replace('"','');

						obj[c[0]] = c[1];

						}
						return obj;
						}

						var cookie = allCookies.getItem("bmccookie");

						var obj = (cookie != null) ? parseCookie(cookie) : {};


						var welcome = "";

						if(obj.hasOwnProperty("fname")) {

						// insert user welcome
						welcome = obj.fname.replace(/\+/g, " ") + " " +
						obj['lname'].replace(/\+/g," ");

						if(jQuery("#welcome").length > 0) {
						jQuery("#welcome").prepend("Welcome
						");
						jQuery("#username").prepend('' + welcome + '');
						}

						// update link url
						jQuery("#loginLink a").text("Log off");
						jQuery("#loginLink a").attr("href", window.location.protocol + "//" +
						window.location.host + "/logoff");

						window.bmcloggedon = true;


						} else if(obj.hasOwnProperty("institution_name")) {

						// insert institution name
						welcome = obj["institution_name"].replace(/\+/g," ");

						if(jQuery("#welcome").length > 0) {
						jQuery("#welcome").prepend("Welcome
						");
						jQuery("#username").prepend('' + welcome + '');
						}

						// update link url
						jQuery("#loginLink a").text("Log on");
						jQuery("#loginLink a").attr("href", window.location.protocol + "//" +
						window.location.host + "/logon");

						window.bmcloggedon = false;


						} else {
						jQuery("#welcome").innerHTML = "";
						jQuery("#loginLink a").text("Log on");
						jQuery("#loginLink a").attr("href", window.location.protocol + "//" +
						window.location.host + "/logon");

						window.bmcloggedon = false;

						}


						// if athens login cookie doesn't exist and the athens login link is
						visible then remove it.
						if(allCookies.hasItem("athens") != true && document.getElementById("loginAthensLink") != null) {
						jQuery("#loginAthensLink").hide();
						}

						}

